.relates		( ( binds ( *helper.T-cell *antigen ) true ) )	if.not.actors	( ( act.h.t-cell ( *act.h.t-cell ) true ) )	then.create	( *act.h.t-cell )	then.relates 	( ( proliferates ( *helper.T-cell ) true ) )		then.actors	( ( act.h.t-cell ( *act.h.t-cell ) true ) )	then.say		" *Helper T-cell activated. "		   	c:	B-CELL.ACTIVATION.1	level			basic	sub.of			phys.process	instance.of		process.RULE			\ NOTE new syntax here	my.creator		jp2 rbt	context			human.immunology	if.actors		( ( B-cell ( *B-cell ) true )				  	( helper.t2-cell ( *h.t-cell ) true ) )		if.relates		( ( ANTIGEN.BINDS ( *act.h.t-cell *B-CELL ) true ) )	if.states		( ( not.activated ( *b-cell ) true ) 					  ( activated ( *h.t-cell ) true ) 					  ( not.activated ( *b-cell ) true ) )	then.states		( ( activated ( *B-cell ) true ) )			then.say		" B-cell activated. "	   	}