ll ) true )				  	  ( helper.t2-cell ( *t-cell ) true ) 				  	  ( Il-4 ( *x ) true )				  	  ( Il-5  ( *y ) true )				  	  ( Il-6 ( *z ) true ) )	if.states		( ( activated ( *t-cell ) true ) )	if.relates		( ( abuts ( *b-cell *t-cell ) true )					  ( abuts ( *b-cell *x ) true )					  ( abuts ( *b-cell *Y ) true ) )	then.create		( *a *b *c )	then.actors		( ( memory-cell ( *a ) true )					  ( antibody ( *b ) true )					  ( plasma-cell ( *c ) true ) )	then.states		( ( proliferates ( *b-cell ) true ) )	then.say		" antibody produced " c:	ANTIGEN.BINDING.2a	level			basic	sub.of			phys.process	instance.of		process.rule \ popsim.rule	my.creator		jp2	context			immunology	if.actors		( ( processed.antigen ( *antigen ) true )				  	  ( B-cell ( *b-cell ) true ) )	if.not.relates	( ( presents ( *b-cell *antigen ) true ) )	if.relates		( ( abuts ( *b-cell *antigen ) true ) )	if.states		( ( activated ( *b-cell ) true ) )	then.states		( ( processes ( *b-cell *antigen ) true ) )	then.say		" processed antigen processed by  b-cell. " c:	ANTIGEN.BINDING.3	level		basic	sub.of		phys.process	instance.of	process.rule \ popsim.rule	my.creator	jp2	context		immunology	if.actors		( ( processed.antigen ( *antigen ) true )			 	  	  ( helper.T1-cell ( *helper.T-cell ) true ) )	if.not.relates	( ( binds ( *helper.T-cell *antigen ) true ) )	if.relates		( ( abuts ( *antigen *helper.t-cell ) true ) )	then.relates	( ( binds ( *helper.T-cell *antigen ) true ) )	then.say		" Antigen bound to helper T-cell. " }comment:	this rule does a good job of making an abuttment, but there may be a problem...	we cannot do an if.relates on something which does not exist	so we choose the if.not.relates relationcomment;c:	ACTIVATED.HELPER.T-CELL.B-CELL.ABUT.1	level			basic	sub.of			phys.process	instance.of		process.rule	my.creator		jp2 rbt	context			human.immunology	if.actors		( ( B-cell ( *B-cell ) true )				 	  ( helper.T1-cell ( *helper.T1-cell ) true ) )	if.not.relates 	( ( abuts ( *b-cell *helper.T1-cell ) true ) )	if.states		( ( activated ( *helper.T1-cell ) true ) ) 	then.relates 	( ( abuts ( *B-cell *helper.T1-cell ) true ) ) 	then.say		" B-cell abuts activated helper T1-cell. "c:	ACTIVATED.HELPER.T-CELL.B-CELL.ABUT.1a	level			basic	sub.of			phys.process	instance.of		process.rule	my.creator		jp2 rbt	context			human.immunology	if.actors		( ( B-cell ( *B-cell ) true )				 	  ( helper.T2-cell ( *helper.T2-cell ) true ) )	if.not.relates 	( ( abuts ( *b-cell *helper.T2-cell ) true ) )	if.states		( ( activated ( *helper.T2-cell ) true ) ) 	then.relates 	( ( abuts ( *B-cell *helper.T2-cell ) true ) ) 	then.say		" B-cell abuts activated helper T2-cell. "\ I suspect this is wrong--I recall reading that the ht-cell gets activated when it antigen-binds\ to a macrophage or b-cell presenting hla and antigen parts\c:	ACTIVATED.HELPER.T-CELL.ANTIGEN.B-CELL.BINDING.1	level			basic	sub.of			phys.process 	instance.of		process.RULE \ popsim.rule		\ jp2	my.creator		jp2 rbt	context			human.immunology	if.actors		( ( B-cell ( *B-cell ) true )				  	  ( ANTIGEN ( *ANTIGEN ) TRUE )	\ jp2 missing actor				 	  ( act.h.t-cell ( *act.h.t-cell ) true ) )	if.relates		( ( presents ( *b-cell *antigen ) true )				  	  ( abuts ( *B-cell *act.h.t-cell ) true ) )\	if.not.relates  ( ( ANTIGEN.BINDS ( *act.h.t-cell *B-CELL ) false ) ) 	if.not.relates  ( ( ANTIGEN.BINDS ( *act.h.t-cell *B-CELL ) true ) )	\ dhf	then.relates 	( ( ANTIGEN.BINDS ( *act.h.t-cell *B-CELL ) true ) )\	THEN.ACTORS		( ( B.AB.T-CELL ( *B-CELL ) true ) )			\ jp2	then.say		" Activated helper T-cell binds B-cell bound antigen."		\ _________________________________________ANTIBODY-PATHOGEN BINDING PROCESSES	\ next rule does not adress the fact that antigen is related--bound--to virusc:	ANTIBODY.VIRUS.ANTIGEN.BINDING.1	level		basic	sub.of		phys.process	instance.of	process.RULE \ popsim.rule	my.creator	jp2 rbt	context		human.immunology	if.actors		( ( antibody ( *antibody ) true )				 ( virus ( *virus ) true ) )	if.not.relates ( ( binds ( *antibody *virus ) true ) )	\ added to make it run faster	then.relates	( ( binds ( *antibody *virus ) true ) )	then.say		" Antibody binds to virus. "	