cell ( *plasma.cell ) true )					 	\ 		   		  ( memory.cell ( *memory.cell ) true ) )	then.create		( *plasma.cell *memory.cell )	then.actors		( ( plasma.cell ( *plasma.cell ) true )				  	( memory.cell ( *memory.cell ) true ) 				  	( blast.cell ( *blast.cell ) false ) )	then.say		" Plasma cell produced.  Memory B-cell produced. "			   	  	c:	B-CELL.CYTOKINE.RECEPTOR.PRODUCTION.1	level			basic	sub.of			phys.process	instance.of		process.rule \ popsim.rule	my.creator		jp2 rbt jwp	context			human.immunology	if.actors		( ( activated.B-cell ( *activated.B-cell ) true )				  	  ( IL-1 ( *IL-1 ) true ) )	\ if.not.actors	( ( cytokine.receptor  ( *cytokine.receptor ) true ) )	\ prevent refiring		then.create		( *cytokine.receptor )	then.actors		( ( cytokine.receptor  ( *cytokine.receptor ) true )					( IL-1 ( *IL-1 ) false ) 					( activated.B-cell ( *activated.B-cell ) false ) )			then.relates	( ( synthesizes ( *cytokine.receptor *activated.B-cell  ) true ) 			 	  	  ( attaches.to ( *cytokine.receptor *IL-1 ) true ) )	then.say		" B-cell synthesizes cytokine receptor, and IL-1 attaches to receptor. "	   			    c:	ANTIGEN.PRESENTATION.B	level		basic	sub.of		phys.process	instance.of	process.rule \ popsim.rule	my.creator	rbt jwp	context		immunology	if.actors		( ( activated.b-cell ( *activated.b-cell ) true ) )	\ if.not.relates	( ( presents ( *antigen *activated.b-cell ) true ) )	\ then.relates 	( ( presents ( *antigen *activated.b-cell ) true ) )	then.actors		( ( activated.b-cell ( *activated.b-cell ) false ) ) 	then.say		" activated B-cell presents processed antigen. "\ ____________________________________________________ANTIBODY PRODUCTIONc: antibody.production.sense	level			basic	sub.of			phys.process	instance.of 	sense.rule	my.creator		jwp	context			immunology	if.actors		( ( plasma.cell ( *plasma.cell ) true )					( IL-1 ( *IL-1 ) true ) )	then.relates	( ( abuts ( *plasma.cell *IL-1 ) true ) )	c:	ANTIBODY.PRODUCTION.1	level		basic	sub.of		phys.process	instance.of	process.RULE \ popsim.rule	my.creator	rbt	context		immunology	if.actors		( ( IL-1 ( *IL-1 ) true )			   		( plasma.cell ( *plasma.cell ) true ) )	if.relates		( ( abuts ( *plasma.cell *IL-1 ) true ) )	if.not.actors	( ( antibody ( *antibody ) true ) )	then.create		( *antibody )	then.actors		( ( antibody ( *antibody ) true ) 					( plasma.cell ( *plasma.cell ) false ) )	then.say		" Plasma cell produces antibody. "