\ ________________________________________________________TRANSPORT PROCESSES\ made these rules one-shot by testing for false relation on primary output relation\ rbt 1/11/93\ modified to use the if.not.relates method--jp2 3/02/93  { c:	ANTIGEN.INTERNALIZATION.B	level		basic	sub.of		phys.process	instance.of	process.rule \ popsim.rule	my.creator	rbt 	context		immunology	if.actors		( ( antigen ( *antigen ) true )			 	 ( b-cell ( *b-cell ) true ) )	if.relates		( ( binds ( *b-cell *antigen ) true ) )	if.not.relates	( ( contains ( *b-cell *antigen ) true ) ) 	then.relates	( ( contains ( *b-cell *antigen ) true ) )	then.say		" B-cell ingests bound antigen. "c:	ANTIGEN.INTERNALIZATION.M	level		basic	sub.of		phys.process	instance.of	process.rule \ popsim.rule	my.creator	rbt 	context		immunology	if.actors		( ( antigen ( *antigen ) true )			 	 ( macrophage ( *macrophage ) true ) )	if.relates		( ( binds ( *macrophage *antigen ) true ) )	if.not.relates	( ( contains ( *macrophage *antigen ) true ) )	then.relates 	( ( contains ( *macrophage *antigen ) true ) )	then.say		" Macrophage ingests bound antigen. "}	